function blog_loaded() {
blog_bg = document.getElementById("blog-bg");
blog_resized();
kar_init();
var milliseconds = window.setInterval(blog_onMillisecond, 20);
blog_search();
if (window.location.href.split("#")[1]) {
document.getElementById("search").value = window.location.href.split("#")[1];
}
}
function blog_resized() {
blog_box = document.documentElement.getBoundingClientRect();
blog_bg.style.width = blog_box.width + "px";
blog_bg.style.height = blog_box.height + "px";
blog_bg.width = blog_box.width;
blog_bg.height = blog_box.height;
}
function blog_onMillisecond() {
kar_yagdir();
}
function blog_searchInit() {
var link = document.createElement("a");
link.href = "#" + document.getElementById("search").value;
link.click();
window.location.reload();
}
function blog_search() {
var searchtext = window.location.href.split("#")[1].toLowerCase();
if (!searchtext) {return;}
var searched = document.getElementsByClassName("ozet-gonderi");
for (var s = 0; s < searched.length; s++) {
var tit = searched[s].getElementsByClassName("tit")[0].innerHTML.toLowerCase();
if (tit.search(searchtext) == -1) {
searched[s].parentNode.removeChild(searched[s]);
}
}
}
