var kar_ctx;
var kar_taneler = [];
var kar_taneAraligi = 150;
var kar_hiz = 3;
var kar_kristalKalinligi = 5;
var kar_taneBoyutu = 6;
var kar_maksDonmeIvmesi = Math.PI / 120;
var kar_sekiller = [
[{baslangic: 0, baslangicAci: 0, uzunluk: 7, aci: 0}, {baslangic: 5, baslangicAci: 0, uzunluk: 3, aci: Math.PI / -3}, {baslangic: 5, baslangicAci: 0, uzunluk: 3, aci: Math.PI / 3}],
[{baslangic: 0, baslangicAci: 0, uzunluk: 4, aci: Math.PI / 9}, {baslangic: 0, baslangicAci: 0, uzunluk: 4, aci: Math.PI / -9}, {baslangic: 4, baslangicAci: Math.PI / -9, uzunluk: 4, aci: Math.PI / 9}, {baslangic: 4, baslangicAci: Math.PI / 9, uzunluk: 4, aci: Math.PI / -9}],
[{baslangic: 0, baslangicAci: 0, uzunluk: 7, aci: 0}, {baslangic: 6, baslangicAci: 0, uzunluk: 6, aci: Math.PI * 2 / 3}, {baslangic: 4, baslangicAci: 0, uzunluk: 4, aci: Math.PI * 2 / 3}, {baslangic: 2, baslangicAci: 0, uzunluk: 2, aci: Math.PI * 2 / 3}]
];
var kar_bulut = -50;
var kar_zemin = 50;
function kar_init() {
while (!kar_yagdir()) {}
kar_ctx = blog_bg.getContext("2d");
}
function kar_yagdir() {
var random = Math.ceil(Math.random() * blog_box.width);
var yakinlardaVar = false;
var out = false;
for (var i = 0; i < kar_taneler.length; i++) {
if (Math.abs(Math.sqrt(((kar_taneler[i].x - random) * (kar_taneler[i].x - random)) + ((kar_taneler[i].y - kar_bulut) * (kar_taneler[i].y - kar_bulut)))) < kar_taneAraligi) {yakinlardaVar = true;}
}
if (!yakinlardaVar) {
var a = {};
a.sekil = Math.floor(Math.random() * kar_sekiller.length);
a.x = random;
a.y = kar_bulut;
a.aci = Math.ceil(Math.random() * Math.PI / 3);
a.donmeIvmesi = Math.random() * kar_maksDonmeIvmesi * 2 - kar_maksDonmeIvmesi;
kar_taneler.push(a);
}
if (kar_ctx) {
kar_ctx.clearRect(0, 0, blog_box.width, blog_box.height);
kar_ctx.strokeStyle = "white";
kar_ctx.lineWidth = kar_kristalKalinligi;
}
for (var i = 0; i < kar_taneler.length; i++) {
kar_taneler[i].aci +=  kar_taneler[i].donmeIvmesi;
if (kar_taneler[i].aci > Math.PI / 180 * 59) {
kar_taneler[i].aci -= Math.PI / 180 * 59;
}
kar_taneler[i].y += kar_hiz;
if (kar_taneler[i].y > blog_box.height + kar_zemin) {
kar_taneler.splice(i, 1);
out = true;
}
if (kar_ctx) {
var taneSekli = kar_sekiller[kar_taneler[i].sekil];
kar_ctx.beginPath();
for (var aci = kar_taneler[i].aci; aci < 2 * Math.PI; aci += Math.PI / 3) {
for (var eleman = 0; eleman < taneSekli.length; eleman++) {
var kar_x = kar_taneler[i].x;
var kar_y = kar_taneler[i].y;
kar_x += taneSekli[eleman].baslangic * Math.cos(aci + taneSekli[eleman].baslangicAci) * kar_taneBoyutu;
kar_y += taneSekli[eleman].baslangic * Math.sin(aci + taneSekli[eleman].baslangicAci) * kar_taneBoyutu;
kar_ctx.moveTo(kar_x, kar_y);
kar_x += taneSekli[eleman].uzunluk * Math.cos(aci + taneSekli[eleman].aci) * kar_taneBoyutu;
kar_y += taneSekli[eleman].uzunluk * Math.sin(aci + taneSekli[eleman].aci) * kar_taneBoyutu;
kar_ctx.lineTo(kar_x, kar_y);
}
}
kar_ctx.stroke();
kar_ctx.closePath();
}
}
return out;
}
function kar_donusumler(kar_tanesi, taneSekli) {
var tanesekil2 = [];
for (var aci = 0; aci < 2 * Math.PI; aci += Math.PI / 3) {
for (var a = 0; a < taneSekli.length; a++) {
taneSekli2.push(taneSekli[a]);
}
}
return tanesekli2;
}

